mapper.select:
select
from tb_qst_mall_order_item where order_id in
#{id}

SELECT c.category_id as categoryId,c.category_name as categoryName,temp.sPrice as price FROM tb_qst_mall_goods_category c LEFT JOIN (SELECT c1.category_id as cId,SUM(oi.selling_price) as sPrice FROM tb_qst_mall_order o LEFT JOIN tb_qst_mall_order_item oi ON o.order_id = oi.order_id LEFT JOIN tb_qst_mall_goods_info g ON oi.goods_id = g.goods_id LEFT JOIN tb_qst_mall_goods_category c3 ON g.goods_category_id = c3.category_id LEFT JOIN tb_qst_mall_goods_category c2 ON c3.parent_id = c2.category_id LEFT JOIN tb_qst_mall_goods_category c1 ON c2.parent_id = c1.category_id WHERE o.is_deleted = 0 AND o.order_status = 4 AND c3.is_deleted = 0 AND c2.is_deleted = 0 AND c1.is_deleted = 0
and o.create_time > #{paramBeginTime}
and o.create_time < #{paramEndTime}
GROUP BY cId) temp ON c.category_id = temp.cId WHERE c.category_level = 1 ORDER BY c.category_id ASC
